import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { StyleSheet, View, ActivityIndicator, Text } from 'react-native';
import { useState } from 'react';
import { createMaterialTopTabNavigator } from '@react-navigation/material-top-tabs';
import { NavigationContainer } from '@react-navigation/native';

const FETCH_SERVER_ADRESS = "http://5.189.129.153:3000"

const Tab = createMaterialTopTabNavigator();

async function postRequest(url = '', data = {}) {
  const response = await fetch(url, {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify(data)
  });
  return response.json();
}

function getCalendarWeek(d) {
  d = new Date(Date.UTC(d.getFullYear(), d.getMonth(), d.getDate()));
  d.setUTCDate(d.getUTCDate() + 4 - (d.getUTCDay() || 7));
  var yearStart = new Date(Date.UTC(d.getUTCFullYear(), 0, 1));
  var weekNo = Math.ceil(((d - yearStart) / 86400000 + 1) / 7);
  return weekNo;
}

function convertWeek(data) {
  let week = [[], [], [], [], []]
  let day = 0
  for (let i = 0; i < 50; i++) {
    if (i > 9) {
      day = 1
    }
    if (i > 19) {
      day = 2
    }
    if (i > 29) {
      day = 3
    }
    if (i > 39) {
      day = 4
    }
    week[day].push({ subject: data[i].subject, room: data[i].room })
  }
  return week
}

function createLesson(day1, day2) {
  let color1 = "white"
  let color2 = "white"
  let color11 = "white"
  let color22 = "white"


  if (day1.subject.includes("oder")) {

    let day1Parts = day1.subject.split("oder")
    let day2Parts = day2.subject.split("oder")



    return (<View style={{ margin: 15 }}>
      <Text style={{ fontSize: 25, color: color1 }}>
        {day1.subject.split("oder")[0]}
        <Text style={{ color: "white" }}>{"oder"}</Text>
        <Text style={{ color: color11 }}>{day1.subject.split("oder")[1]}</Text>
        <Text style={{ color: "#726a95" }}>{" | "}</Text>
        <Text style={{ color: "#59886b" }}>
          {day1.room}
        </Text>

      </Text>

      <Text style={{ fontSize: 25, color: color1 }}>
        {day2.subject.split("oder")[0]}
        <Text style={{ color: "white" }}>{"oder"}</Text>
        <Text style={{ color: color22 }}>{day2.subject.split("oder")[1]}</Text>
        <Text style={{ color: "#726a95" }}>{" | "}</Text>
        <Text style={{ color: "#59886b" }}>
          {day2.room}
        </Text>

      </Text>
    </View>)
  } else {
    

    return (<View style={{ margin: 15 }}>
      <Text style={{ fontSize: 25, color: color1 }}>
        {day1.subject}
        <Text style={{ color: "#726a95" }}>{" | "}</Text>
        <Text style={{ color: "#59886b" }}>
          {day1.room}
        </Text>

      </Text>

      <Text style={{ fontSize: 25, color: color2 }}>
        {day2.subject}
        <Text style={{ color: "#726a95" }}>{" | "}</Text>
        <Text style={{ color: "#59886b" }}>

          {day2.room}

        </Text>

      </Text>
    </View>)
  }

}

function lessons({ route }) {
  const { day } = route.params;
  return (<View style={{ flex: 1, alignItems: "center", backgroundColor: "#2C2F33", width: "100%", height: "100%" }}>
    {createLesson(day[0], day[1])}
    <View style={styles.pauseView}>
      <Text style={styles.pauseText}>{"Pause"}</Text>
    </View>
    {createLesson(day[2], day[3])}
    <View style={styles.pauseView}>
      <Text style={styles.pauseText}>{"Pause"}</Text>
    </View>
    {createLesson(day[4], day[5])}
    <View style={styles.pauseView}>
      <Text style={styles.pauseText}>{"Pause"}</Text>
    </View>
    {createLesson(day[6], day[7])}
    <View style={styles.pauseView}>
      <Text style={styles.pauseText}>{"Pause"}</Text>
    </View>
    {createLesson(day[8], day[9])}
  </View >)
}




function timetable(weeks) {
  console.log("executed timetable.")
  let skip = 0
  let currDate = new Date();
  let calendarWeek = 1;
  let currDay = currDate.getDay()
  if(currDay == 0 || currDay == 6){
    skip++
  }
  if ((getCalendarWeek(currDate) + skip) % 2 === 0) calendarWeek = 0;
  let week = weeks[calendarWeek]
  const daysForIndex = ["Montag", "Dienstag", "Mittwoch", "Donnerstag", "Freitag"]
  return (
    <NavigationContainer>
      <StatusBar hidden={true} />
      <Tab.Navigator initialRouteName={daysForIndex[currDate.getDay() - 1]} tabBarOptions={{
        scrollEnabled: true,
        activeTintColor: "#7289DA",
        inactiveTintColor: "#99AAB5",
        indicatorStyle: {
          backgroundColor: "#7289DA"
        },
        style: {
          backgroundColor: "#23272A",
        }
      }
      }>
        <Tab.Screen
          name="Montag"
          component={lessons}
          initialParams={{
            day: week[0],
          }}
        />
        <Tab.Screen
          name="Dienstag"
          component={lessons}
          initialParams={{
            day: week[1],
          }}
        />
        <Tab.Screen
          name="Mittwoch"
          component={lessons}
          initialParams={{
            day: week[2],
          }}
        />
        <Tab.Screen
          name="Donnerstag"
          component={lessons}
          initialParams={{
            day: week[3],
          }}
        />
        <Tab.Screen
          name="Freitag"
          component={lessons}
          initialParams={{
            day: week[4],
          }}
        />
      </Tab.Navigator>
    </NavigationContainer >

  );
}

function loadingScreen() {
  console.log("executed loadingScreen.")
  const [isLoadingState, setLoadingState] = useState(true);
  const [text, setText] = useState("aktualisiere Stundenplan Daten...");
  const [weeks, setWeeks] = useState([]);
  let fetchingIntervalNumber = setInterval(() => {
    postRequest(FETCH_SERVER_ADRESS, { request: "data" }).then(data => {
      setLoadingState(false)
      setText("Fertig.")
      setTimeout(() => {
        let evenWeek = convertWeek(data[0])
        let oddWEeek = convertWeek(data[1])
        setWeeks([evenWeek, oddWEeek])
        clearInterval(fetchingIntervalNumber)
      }, 500)
      return (timetable(weeks))
    });
  }, 2500);
  return (
    <View style={styles.container}>
      <StatusBar hidden={true} />
      <ActivityIndicator hidesWhenStopped={true} animating={isLoadingState} size="large" color="#7289DA" />
      <Text style={styles.text}>{text}</Text>
    </View>
  );
  

}

export default function App() {
  return (
    <View style={styles.container}>
      {loadingScreen()}
    </View>
  );
}
























const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#2C2F33',
    justifyContent: 'center',
  },
  text: {
    alignSelf: "center",
    color: "white",
    margin: 20
  },
  subjectText: {
    fontSize: 25,
    color: "white"
  },
  pauseText: {
    fontSize: 30,
    color: "#7289DA"
  },
  subjectView: {
    margin: 15,
  },
  pauseView: {
    marginLeft: 15,
    marginRight: 15,
  }
});
